import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { CountriesService } from '../../services/countries.service';
import { switchMap } from 'rxjs';
import { Country } from '../../interfaces/country';

@Component({
  selector: 'app-country-page',
  templateUrl: './country-page.component.html',
  styles: [
  ]
})
export class CountryPageComponent implements OnInit {

  public country?: Country;

  constructor( private activatedRoute   : ActivatedRoute,
               private router           : Router,
               private countriesService : CountriesService){}

  ngOnInit(): void {
    this.activatedRoute.params
    .pipe(
      switchMap( ({ id }) => this.countriesService.searchCountryByAlphaCode( id ) )
    )
    .subscribe( country => {
      if ( !country ) { return this.router.navigateByUrl(''); }
      return this.country = country;
    });
  }

}






<ng-template #loading>
  <div class="alert alert-info text-center">
    Espere por favor
  </div>
</ng-template>



<div *ngIf="country; else loading">


<div>
  <div class="row">
    <div class="col-12">
      <h2>País:<strong>{{ country.name.common }}</strong></h2>
    </div>
    <hr>
  </div>
</div>

<div class="row">
  <div class="col-4">
    <h3>bandera:</h3>
    <img [src]="country.flags.svg" [alt]="country.name.common" class="img-thumbnail">
  </div>
  <div class="col">
    <h3></h3>
    <ul class="list-group">
      <li class="list-group-item">
        <strong>Población:</strong> {{ country.population | number }}
      </li>
      <li class="list-group-item">
        <strong>Capital:</strong> {{ country.capital }}
      </li>
      <li class="list-group-item">
        <strong>Codigo:</strong> {{ country.cca3 }}
      </li>
      <li class="list-group-item">
        <strong>Continente:</strong> {{ country.region }}
      </li>
      <li class="list-group-item">
        <strong>Región:</strong> {{ country.subregion }}
      </li>
      <li class="list-group-item">
        <strong>Población:</strong> {{ country.population | number }}
      </li>
    </ul>
  </div>
</div>

</div>
